const socket = io();

const PC = new window.RTCPeerConnection();

navigator.getUserMedia(
  // Premissions
  { video: true, audio: true },

  // IF(Resolve)
  (stream) => {
    const localVideo = document.getElementById("local-video");

    // Pass to 'Video' Tag
    if (localVideo) {
      localVideo.srcObject = stream;
    }
  },

  // IF(Reject)
  (err) => {
    console.log(err.message);
  }
);
